export function generate(pkgInfo, exportName) {
    if (pkgInfo.type === 'header-only') {
        let includeDirs = pkgInfo['paths']['includes']
            .map(x => `list(APPEND ${exportName}_INCLUDE_DIRS [=[${x}]=])`)
            .join('\n');

        return `
#
# Generated by Zepo Package Manager
#

add_library(${exportName} INTERFACE)

set(${exportName}_INCLUDE_DIRS)
${includeDirs}

target_include_directories (${exportName} INTERFACE $\{${exportName}_INCLUDE_DIRS\})

        `;
    }

    return `message (FATAL "Unsupported type ${pkgInfo['type']}")`;
}